mapper.select:
select blog.* ,category.name as categoryName,user.name as userName from blog left join category on category_id=category.id left join user on user_id=user.id
and title like concat('%',#{title},'%') and user.name like concat('%',#{user.name},'%') and category.name like concat('%',#{categoryName},'%') and user_id=#{userId}
order by id desc
